// geoprumo/frontend/src/App.jsx (versão final com nova lógica)

import React, { useState } from 'react';
import Uploader from './components/Upload/Uploader';
import PointsList from './components/Points/PointsList'; // Importa a lista
import { optimizeRouteData, enrichRouteWithAI } from './services/api';

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [isEnriching, setIsEnriching] = useState(false);
  const [optimizedData, setOptimizedData] = useState(null);
  const [error, setError] = useState(null);

  // Função para o primeiro botão: "Analisar e Otimizar"
  const handleOptimize = async (payload) => {
    setIsLoading(true);
    setError(null);
    setOptimizedData(null);
    try {
      const result = await optimizeRouteData(payload);
      setOptimizedData(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Função para o segundo botão: "Enriquecer com IA"
  const handleEnrich = async () => {
    if (!optimizedData || !optimizedData.optimized_route) return;
    
    setIsEnriching(true);
    setError(null);
    try {
      const enrichedPoints = await enrichRouteWithAI(optimizedData.optimized_route);
      // Atualiza o estado com os pontos enriquecidos
      setOptimizedData(prevData => ({
        ...prevData,
        optimized_route: enrichedPoints,
      }));
    } catch (err) {
      setError(err.message);
    } finally {
      setIsEnriching(false);
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen flex flex-col font-sans">
      <header className="bg-white shadow-md p-4 flex items-center">
        <img src="/GeoPrumo pin.png" alt="Logo GeoPrumo" className="h-10 w-10 mr-4"/>
        <img src="/GeoPrumo completa.jpg" alt="Logotipo Completo GeoPrumo" className="h-10 hidden sm:block"/>
      </header>

      <main className="flex-grow container mx-auto p-4 grid grid-cols-1 lg:grid-cols-2 gap-4">
        <div className="flex flex-col h-[85vh]">
          <div className="bg-white p-4 rounded-lg shadow-md mb-4">
            <h2 className="text-xl font-bold text-gray-700 mb-2">1. Adicionar Pontos</h2>
            <Uploader onProcess={handleOptimize} isLoading={isLoading} />
            {error && <div className="mt-2 text-red-600 font-bold">{error}</div>}
          </div>
          
          <div className="bg-white p-4 rounded-lg shadow-md flex-grow overflow-y-auto">
            {/* Renderiza a lista de pontos com os dados da otimização */}
            <PointsList 
              route={optimizedData?.optimized_route} 
              onEnrich={handleEnrich}
              isEnriching={isEnriching}
            />
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow-md h-[85vh]">
          <h2 className="text-xl font-bold text-gray-700 mb-2">Mapa da Rota</h2>
          <div className="bg-gray-200 h-full rounded flex items-center justify-center">
            <p className="text-gray-500">Componente do Mapa</p>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
