// geoprumo/frontend/src/App.jsx

import React, { useState } from 'react';
import Uploader from './components/Upload/Uploader'; // 1. Importa o novo componente

function App() {
  const [isLoading, setIsLoading] = useState(false);
  // Estados para os resultados serão adicionados aqui
  // const [optimizedRoute, setOptimizedRoute] = useState(null);

  // 2. Função que será passada para o Uploader
  const handleProcessData = (payload) => {
    console.log("Dados recebidos do Uploader:", payload);
    setIsLoading(true);

    // Lógica para chamar o backend virá aqui
    
    // Simula uma chamada de API
    setTimeout(() => {
      setIsLoading(false);
      console.log("Processamento finalizado (simulação).");
    }, 3000); // 3 segundos de loading
  };

  return (
    <div className="bg-gray-100 min-h-screen flex flex-col font-sans">
      <header className="bg-white shadow-md p-4 flex items-center">
        <img src="/GeoPrumo pin.png" alt="Logo GeoPrumo" className="h-10 w-10 mr-4"/>
        <img src="/GeoPrumo completa.jpg" alt="Logotipo Completo GeoPrumo" className="h-10 hidden sm:block"/>
      </header>

      <main className="flex-grow container mx-auto p-4 grid grid-cols-1 lg:grid-cols-2 gap-4">
        <div className="flex flex-col h-[85vh]">
          <div className="bg-white p-4 rounded-lg shadow-md mb-4">
            <h2 className="text-xl font-bold text-gray-700 mb-2">1. Adicionar Pontos</h2>
            {/* 3. Renderiza o Uploader e passa a função e o estado para ele */}
            <Uploader onProcess={handleProcessData} isLoading={isLoading} />
          </div>
          
          <div className="bg-white p-4 rounded-lg shadow-md flex-grow overflow-y-auto">
            <h2 className="text-xl font-bold text-gray-700 mb-2">2. Pontos da Rota</h2>
            <div className="bg-gray-200 h-64 rounded flex items-center justify-center">
              <p className="text-gray-500">Lista de Pontos</p>
            </div>
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow-md h-[85vh]">
          <h2 className="text-xl font-bold text-gray-700 mb-2">Mapa da Rota</h2>
          <div className="bg-gray-200 h-full rounded flex items-center justify-center">
            <p className="text-gray-500">Componente do Mapa</p>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
