// geoprumo/frontend/src/components/Points/PointsList.jsx

import React from 'react';

// Ícones para as ações
const TrashIcon = () => (
  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"></path>
  </svg>
);

function PointsList({ route, onEnrich, isEnriching, onDelete, onToggleActive }) {
  if (!route || route.length === 0) {
    return (
      <div className="flex flex-col h-full">
        <h2 className="text-xl font-bold text-gray-700 mb-2">2. Pontos da Rota</h2>
        <div className="bg-gray-100 p-4 rounded-lg flex items-center justify-center h-full">
          <p className="text-gray-500">Aguardando pontos para exibir a rota...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      <div className="flex justify-between items-center mb-2">
        <h2 className="text-xl font-bold text-gray-700">2. Pontos da Rota</h2>
        <button
          onClick={onEnrich}
          disabled={isEnriching}
          className="bg-purple-600 text-white font-bold py-2 px-3 rounded-lg hover:bg-purple-700 disabled:bg-gray-400 flex items-center text-sm transition-colors"
        >
          {isEnriching ? 'Enriquecendo...' : '✨ Enriquecer com IA'}
        </button>
      </div>

      <div className="overflow-y-auto flex-grow">
        <ul>
          {route.map((point) => (
            <li 
              key={`${point.original_index}-${point.order}`} 
              className={`p-3 mb-2 rounded-lg shadow-sm border transition-opacity ${point.active === false ? 'opacity-50 bg-gray-100' : 'bg-white border-gray-200'}`}
            >
              <div className="flex items-start">
                <span className={`text-white rounded-full h-6 w-6 text-sm flex items-center justify-center font-bold mr-3 ${point.active === false ? 'bg-gray-400' : 'bg-blue-500'}`}>
                  {point.order}
                </span>
                <div className="flex-grow">
                  <p className="font-semibold text-gray-800">{point.name}</p>
                  <p className="text-xs text-gray-500">
                    Lat: {point.latitude.toFixed(5)}, Lon: {point.longitude.toFixed(5)}
                  </p>
                  {point.address && (
                    <p className="text-xs text-gray-600 mt-1">
                      <strong>Endereço:</strong> {point.address}
                    </p>
                  )}
                </div>
                {/* Ações do Ponto */}
                <div className="flex items-center space-x-2 ml-2">
                  {/* Toggle Switch */}
                  <button onClick={() => onToggleActive(point.original_index)} className={`relative inline-flex items-center h-5 rounded-full w-9 transition-colors ${point.active === false ? 'bg-gray-300' : 'bg-green-500'}`}>
                    <span className={`inline-block w-3 h-3 transform bg-white rounded-full transition-transform ${point.active === false ? 'translate-x-1' : 'translate-x-5'}`} />
                  </button>
                  {/* Botão Excluir */}
                  <button onClick={() => onDelete(point.original_index)} className="text-red-500 hover:text-red-700">
                    <TrashIcon />
                  </button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default PointsList;
